mapper.select:
select s.*, c.name as categoryName from setmeal s left outer join category c on s.category_id = c.id


        and s.name like concat('%', #{name} ,'%')


        and category_id = #{categoryId}


        and s.status = #{status}


order by create_time desc

select *
from setmeal s
where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.update:
update setmeal
description = #{description},
status = #{status},
category_id = #{categoryId}, name = #{name}, price = #{price}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

